CREATE PROCEDURE [shared].[LoadRolePermissions]
(
	@dbName nvarchar(64)
	, @debug bit = 0
)
AS
BEGIN

SET NOCOUNT ON

--	Permissions will be assigned per column, per role.
--	Columns are retrieved from the information schema.
--	Roles are specified in a custom table.

DELETE FROM shared.RolePermissions WHERE DatabaseName = @dbName
DBCC CHECKIDENT ('shared.RolePermissions', RESEED, 1)

DECLARE @SQLString nvarchar(max) = ''

SET @SQLString += '
WITH RoleNames AS
(
SELECT
	RoleName = [name]
FROM
	' + @dbName + '.sys.database_principals
WHERE 1=1
	AND [type] = ''R''
	AND is_fixed_role = 0
	AND [name] IN (''BBHDashboard'',''Coordinatie'',''Finance'',''HumanResources'',''Operations'')
)

SELECT
	DatabaseName = ''' + @dbName + '''
	, SchemaName = C.TABLE_SCHEMA
	, TableName = C.TABLE_NAME
	, ColumnName = C.COLUMN_NAME
	, RoleName = R.RoleName
	, OrdinalPosition = C.ORDINAL_POSITION
FROM
	' + @dbName + '.INFORMATION_SCHEMA.TABLES T
	LEFT OUTER JOIN ' + @dbName + '.INFORMATION_SCHEMA.COLUMNS C ON T.TABLE_SCHEMA = C.TABLE_SCHEMA AND T.TABLE_NAME = C.TABLE_NAME
	CROSS JOIN RoleNames R
WHERE 1=1
	AND T.TABLE_TYPE = ''BASE TABLE''
	AND C.TABLE_SCHEMA IN (''Dim'', ''Fact'')'

--	Insert columns and roles; by default, no permissions are assigned. 

INSERT INTO
	shared.RolePermissions
	(
	DatabaseName
	, SchemaName
	, TableName
	, ColumnName
	, RoleName
	, OrdinalPosition
	)
EXEC (@SQLString)

