CREATE PROCEDURE [Load].[LoadRolePermissions]
(
	@debug bit = 0
)
AS
BEGIN

SET NOCOUNT ON

--	Permissions will be assigned per column, per role.
--	Columns are retrieved from the information schema.
--	Roles are specified in a custom table.

DELETE FROM [Load].RolePermissions
DBCC CHECKIDENT ('[Load].RolePermissions', RESEED, 1)

;WITH RoleNames AS
(
SELECT
	RoleName = [name]
FROM
	sys.database_principals
WHERE 1=1
	AND [type] = 'R'
	AND is_fixed_role = 0
	AND [name] IN ('BBHDashboard','Coordinatie','Finance','HumanResources','Operations')
)

--	Insert columns and roles; by default, no permissions are assigned. 
INSERT INTO
	[Load].RolePermissions
	(
	SchemaName
	, TableName
	, ColumnName
	, RoleName
	, OrdinalPosition
	)
SELECT
	SchemaName = C.TABLE_SCHEMA
	, TableName = C.TABLE_NAME
	, ColumnName = C.COLUMN_NAME
	, RoleName = R.RoleName
	, OrdinalPosition = C.ORDINAL_POSITION
FROM
	INFORMATION_SCHEMA.TABLES T
	LEFT OUTER JOIN INFORMATION_SCHEMA.COLUMNS C ON T.TABLE_SCHEMA = C.TABLE_SCHEMA AND T.TABLE_NAME = C.TABLE_NAME
	CROSS JOIN RoleNames R
WHERE 1=1
	AND T.TABLE_TYPE = 'BASE TABLE'
	AND C.TABLE_SCHEMA IN ('Dim', 'Fact')

